value.css:
--gray: rgb(107, 114, 128);
--space-1: 0.25rem;
--space-2: 0.5rem;
--space-3: 0.75rem;
--space-4: 1rem;
--space-5: 1.5rem;
--space-6: 2rem;
--space-7: 3rem;
--space-8: 5rem;

section {

}

.container {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: var(--space-8);
  padding: var(--space-8) 0;
}

.content {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  position: relative;

  nav {
  	width: 100%;
    border-bottom: 1px solid #eee;
    padding-bottom: 0.75rem;
    margin-bottom: 1rem;
    ul {
      display: flex;
      li {
        margin-right: 3rem;
        a {

        }
      }
    }
  }

  main {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: center;

    :is(h1, h2, h3) {
      font-weight: 700;
      letter-spacing: 0.025em;
      font-size: 2rem;
      margin-bottom: 1rem;
    }

    .body {
      margin-bottom: 2rem;
    	letter-spacing: 0.025em;
    	color: var(--gray);
    }

  }
}

@media (max-width: 900px) {
  .container {
		grid-template-columns: auto;
  }
}

.body {
	ul {
    	@apply list-disc list-inside;

        li {@apply my-2}

        li > ul {
        	@apply pl-8;
        }
    }

    h2 {
    	@apply text-3xl font-semibold mb-4;
    }
}

