styles.final:
/* Default content styles */

--gray: rgb(107, 114, 128);

--space-1: 0.25rem;

--space-2: 0.5rem;

--space-3: 0.75rem;

--space-4: 1rem;

--space-5: 1.5rem;

--space-6: 2rem;

--space-7: 3rem;

--space-8: 5rem;

.primo-copy {
  width: 100%;
  margin-right: auto;
  margin-left: auto;
  padding-right: 2rem;
  padding-left: 2rem
}

@media (min-width: 640px) {
  .primo-copy {
    max-width: 640px
  }
}

@media (min-width: 768px) {
  .primo-copy {
    max-width: 768px
  }
}

@media (min-width: 1024px) {
  .primo-copy {
    max-width: 1024px
  }
}

@media (min-width: 1280px) {
  .primo-copy {
    max-width: 1280px
  }
}

@media (min-width: 1536px) {
  .primo-copy {
    max-width: 1536px
  }
}

.primo-copy {
  font-size: 1.125rem;
  line-height: 1.75rem;
  --tw-text-opacity: 1;
  color: rgba(55, 65, 81, var(--tw-text-opacity));
}

.primo-copy h1 {
    font-weight: 500;
    font-size: 3rem;
    line-height: 1;
    margin-bottom: 1.5rem;
  }

.primo-copy h2 {
    font-weight: 500;
    font-size: 1.5rem;
    line-height: 2rem;
  }

.primo-copy ol {
    list-style-type: decimal;
  }

.primo-copy ul {
    list-style-position: inside;
    list-style-type: disc;
  }

.primo-copy ul p {
      display: inline;
    }

.primo-copy ol {
    list-style-position: inside;
    list-style-type: decimal;
  }

.primo-copy a {
    --tw-text-opacity: 1;
    color: rgba(37, 99, 235, var(--tw-text-opacity));
    text-decoration: underline;
  }

.primo-copy blockquote {
    padding: 1.5rem;
    --tw-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    box-shadow: var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000), var(--tw-shadow);
  }

.primo-copy mark {
    --tw-bg-opacity: 1;
    background-color: rgba(253, 230, 138, var(--tw-bg-opacity));
    --tw-text-opacity: 1;
    color: rgba(17, 24, 39, var(--tw-text-opacity));
  }

.primo-copy p {
    padding-top: 0.5rem;
    padding-bottom: 0.5rem;
  }

